contracts/ComplianceService.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "./interfaces/IComplianceService.sol";
import "./interfaces/IRegistryService.sol";
import "./interfaces/ITrustService.sol";
import "./libraries/Constants.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title ComplianceService
 * @dev 규제 검증 서비스
 * DS 프로토콜의 Compliance 서비스 구현
 */
contract ComplianceService is IComplianceService, Ownable, ReentrancyGuard {
    // 외부 서비스 참조
    IRegistryService public registry;
    ITrustService public trust;

    // 컴플라이언스 모드
    uint256 public complianceMode; // 0: 화이트리스트, 1: 규제형

    // 국가별 허용 정책
    mapping(string => bool) public countryCompliance;

    // 보유자 수 상한
    uint256 public maxHolders = 200;

    // 거래 한도
    uint256 public maxTransactionAmount = 1000000 * 10**18; // 1M 토큰
    uint256 public dailyTransactionLimit = 10000000 * 10**18; // 10M 토큰

    // 일일 거래량 추적
    mapping(address => mapping(uint256 => uint256)) public dailyTransactionVolume; // address -> date -> volume

    // 블랙리스트
    mapping(address => bool) public blacklistedAddresses;

    // 쿨다운 기간 (초)
    uint256 public cooldownPeriod = 86400; // 24시간

    // 마지막 거래 시간 추적
    mapping(address => uint256) public lastTransactionTime;

    constructor(
        address initialOwner,
        address _registry,
        address _trust
    ) Ownable(initialOwner) {
        registry = IRegistryService(_registry);
        trust = ITrustService(_trust);
        complianceMode = Constants.COMPLIANCE_MODE_REGULATORY; // 기본값: 규제형 모드
    }

    /**
     * @dev 발행 사전 검증 (뷰 함수)
     */
    function preIssuanceCheck(address to, uint256 value) external view override returns (bool ok, string memory reason) {
        return _validateIssuanceInternal(to, value);
    }

    /**
     * @dev 전송 사전 검증 (뷰 함수)
     */
    function preTransferCheck(address from, address to, uint256 value) external view override returns (bool ok, string memory reason) {
        return _validateTransferInternal(from, to, value);
    }

    /**
     * @dev 발행 검증 (트랜잭션 경로)
     */
    function validateIssuance(address to, uint256 value) external override returns (bool ok, string memory reason) {
        return _validateIssuanceInternal(to, value);
    }

    /**
     * @dev 전송 검증 (트랜잭션 경로)
     */
    function validateTransfer(address from, address to, uint256 value) external override returns (bool ok, string memory reason) {
        (bool valid, string memory reasonStr) = _validateTransferInternal(from, to, value);

        if (valid) {
            // 거래량 추적 업데이트
            _updateTransactionVolume(from, to, value);
        }

        return (valid, reasonStr);
    }

    /**
     * @dev 소각 검증 (트랜잭션 경로)
     */
    function validateBurn(address who, uint256 value) external override returns (bool ok, string memory reason) {
        // 소각은 발행사/마스터만 가능
        uint256 role = trust.roleOf(msg.sender);
        if (role != 2 && role != 1) { // ROLE_ISSUER = 2, ROLE_MASTER = 1
            return (false, "Not authorized to burn");
        }

        // 기본 검증
        if (value == 0) {
            return (false, "Cannot burn zero amount");
        }

        return (true, "");
    }

    /**
     * @dev 강제 회수 검증 (트랜잭션 경로)
     */
    function validateSeize(address from, address to, uint256 value) external override returns (bool ok, string memory reason) {
        // 강제 회수는 발행사/마스터만 가능
        uint256 role = trust.roleOf(msg.sender);
        if (role != 2 && role != 1) { // ROLE_ISSUER = 2, ROLE_MASTER = 1
            return (false, "Not authorized to seize");
        }

        // 기본 검증
        if (value == 0) {
            return (false, "Cannot seize zero amount");
        }

        if (from == address(0) || to == address(0)) {
            return (false, "Invalid addresses");
        }

        return (true, "");
    }

    /**
     * @dev 국가 컴플라이언스 정책 설정
     */
    function setCountryCompliance(string calldata country, bool allowed) external override onlyOwner {
        countryCompliance[country] = allowed;
        emit PolicyChanged("country_compliance", abi.encode(country, allowed));
    }

    /**
     * @dev 보유자 수 상한 설정
     */
    function setMaxHolders(uint256 _maxHolders) external override onlyOwner {
        maxHolders = _maxHolders;
        emit PolicyChanged("max_holders", abi.encode(_maxHolders));
    }

    /**
     * @dev 검증 모드 설정
     */
    function setComplianceMode(uint256 mode) external override onlyOwner {
        require(mode <= 1, "Invalid compliance mode");
        complianceMode = mode;
        emit PolicyChanged("compliance_mode", abi.encode(mode));
    }

    /**
     * @dev 거래 한도 설정
     */
    function setTransactionLimits(uint256 _maxTransactionAmount, uint256 _dailyTransactionLimit) external onlyOwner {
        maxTransactionAmount = _maxTransactionAmount;
        dailyTransactionLimit = _dailyTransactionLimit;
        emit PolicyChanged("transaction_limits", abi.encode(_maxTransactionAmount, _dailyTransactionLimit));
    }

    /**
     * @dev 쿨다운 기간 설정
     */
    function setCooldownPeriod(uint256 _cooldownPeriod) external onlyOwner {
        cooldownPeriod = _cooldownPeriod;
        emit PolicyChanged("cooldown_period", abi.encode(_cooldownPeriod));
    }

    /**
     * @dev 주소 블랙리스트 설정
     */
    function setBlacklistedAddress(address addr, bool blacklisted) external onlyOwner {
        blacklistedAddresses[addr] = blacklisted;
        emit PolicyChanged("blacklist", abi.encode(addr, blacklisted));
    }

    /**
     * @dev 외부 서비스 주소 업데이트
     */
    function updateServices(address _registry, address _trust) external onlyOwner {
        registry = IRegistryService(_registry);
        trust = ITrustService(_trust);
        emit PolicyChanged("services_updated", abi.encode(_registry, _trust));
    }

    /**
     * @dev 내부 발행 검증 로직
     */
    function _validateIssuanceInternal(address to, uint256 value) internal view returns (bool ok, string memory reason) {
        // 기본 검증
        if (to == address(0)) {
            return (false, "Cannot issue to zero address");
        }

        if (value == 0) {
            return (false, "Cannot issue zero amount");
        }

        // 블랙리스트 체크
        if (blacklistedAddresses[to]) {
            return (false, "Recipient is blacklisted");
        }

        // 화이트리스트 모드
        if (complianceMode == Constants.COMPLIANCE_MODE_WHITELIST) {
            if (!registry.isWalletRegistered(to)) {
                return (false, "Recipient not in whitelist");
            }

            // 투자자 속성 검증
            string memory investorId = registry.investorOf(to);
            if (!registry.isAttributeValid(investorId, Constants.ATTR_KYC)) {
                return (false, "Recipient KYC not valid");
            }
        }

        // 규제형 모드
        if (complianceMode == Constants.COMPLIANCE_MODE_REGULATORY) {
            // 국가 제한 체크
            string memory investorId = registry.investorOf(to);
            string memory country = registry.getCountry(investorId);

            if (bytes(country).length > 0 && !countryCompliance[country]) {
                return (false, "Country not allowed");
            }

            // 적격 투자자 체크
            if (!registry.isAttributeValid(investorId, Constants.ATTR_ACCREDITED)) {
                return (false, "Recipient not accredited investor");
            }
        }

        return (true, "");
    }

    /**
     * @dev 내부 전송 검증 로직
     */
    function _validateTransferInternal(address from, address to, uint256 value) internal view returns (bool ok, string memory reason) {
        // 기본 검증
        if (from == address(0) || to == address(0)) {
            return (false, "Invalid addresses");
        }

        if (value == 0) {
            return (false, "Cannot transfer zero amount");
        }

        // 블랙리스트 체크
        if (blacklistedAddresses[from] || blacklistedAddresses[to]) {
            return (false, "Address is blacklisted");
        }

        // 거래 한도 체크
        if (value > maxTransactionAmount) {
            return (false, "Transaction amount exceeds limit");
        }

        // 쿨다운 체크
        if (block.timestamp - lastTransactionTime[from] < cooldownPeriod) {
            return (false, "Cooldown period not expired");
        }

        // 화이트리스트 모드
        if (complianceMode == Constants.COMPLIANCE_MODE_WHITELIST) {
            if (!registry.isWalletRegistered(from) || !registry.isWalletRegistered(to)) {
                return (false, "Address not in whitelist");
            }

            // 송신인 KYC 검증
            string memory fromInvestorId = registry.investorOf(from);
            if (!registry.isAttributeValid(fromInvestorId, Constants.ATTR_KYC)) {
                return (false, "Sender KYC not valid");
            }

            // 수신인 KYC 검증
            string memory toInvestorId = registry.investorOf(to);
            if (!registry.isAttributeValid(toInvestorId, Constants.ATTR_KYC)) {
                return (false, "Recipient KYC not valid");
            }
        }

        // 규제형 모드
        if (complianceMode == Constants.COMPLIANCE_MODE_REGULATORY) {
            // 국가 제한 체크
            string memory fromInvestorId = registry.investorOf(from);
            string memory toInvestorId = registry.investorOf(to);

            string memory fromCountry = registry.getCountry(fromInvestorId);
            string memory toCountry = registry.getCountry(toInvestorId);

            if (bytes(fromCountry).length > 0 && !countryCompliance[fromCountry]) {
                return (false, "Sender country not allowed");
            }

            if (bytes(toCountry).length > 0 && !countryCompliance[toCountry]) {
                return (false, "Recipient country not allowed");
            }
        }

        return (true, "");
    }

    /**
     * @dev 거래량 추적 업데이트
     */
    function _updateTransactionVolume(address from, address to, uint256 value) internal {
        uint256 today = block.timestamp / 86400; // 일 단위로 변환

        // 송신인 일일 거래량 업데이트
        dailyTransactionVolume[from][today] += value;

        // 일일 한도 체크
        if (dailyTransactionVolume[from][today] > dailyTransactionLimit) {
            emit ComplianceViolation("Daily transaction limit exceeded", from, to, value);
        }

        // 마지막 거래 시간 업데이트
        lastTransactionTime[from] = block.timestamp;
    }

    /**
     * @dev 일일 거래량 조회
     */
    function getDailyTransactionVolume(address addr, uint256 date) external view returns (uint256) {
        return dailyTransactionVolume[addr][date];
    }

    /**
     * @dev 주소의 쿨다운 상태 조회
     */
    function getCooldownStatus(address addr) external view returns (bool canTransact, uint256 remainingTime) {
        uint256 timeSinceLastTransaction = block.timestamp - lastTransactionTime[addr];

        if (timeSinceLastTransaction >= cooldownPeriod) {
            return (true, 0);
        }

        return (false, cooldownPeriod - timeSinceLastTransaction);
    }
}

contracts/DSToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./interfaces/IComplianceService.sol";
import "./interfaces/ITrustService.sol";
import "./interfaces/IRegistryService.sol";
import "./libraries/Constants.sol";

/**
 * @title DSToken
 * @dev ERC-20 기반 증권형 토큰
 * DS 프로토콜의 DS Token 구현
 */
contract DSToken is ERC20, Pausable, ReentrancyGuard {
    // 외부 서비스 참조
    ITrustService public trust;
    IRegistryService public registry;
    IComplianceService public compliance;

    // 토큰 정보
    uint8 private immutable _decimals;
    uint256 public totalIssued; // 누적 발행량

    // 보유자 관리
    address[] private _holders;
    mapping(address => bool) private _isHolder;

    // 락킹 정보
    struct LockInfo {
        uint256 amountLocked;
        uint64 releaseTime;
        string reason;
    }
    mapping(address => LockInfo) private _locks;


    // 이벤트
    event Issued(address indexed to, uint256 value);
    event IssuedWithLock(address indexed to, uint256 minted, uint256 locked, uint64 releaseTime, string reason);
    event Burned(address indexed who, uint256 value, string reason);
    event Seized(address indexed from, address indexed to, uint256 value, string reason);
    event LockSet(address indexed to, uint256 amount, uint64 releaseTime, string reason);
    event LockReleased(address indexed to, uint256 amount);

    constructor(
        string memory name,
        string memory symbol,
        uint8 decimals_,
        address _trust,
        address _registry,
        address _compliance
    ) ERC20(name, symbol) {
        _decimals = decimals_;
        trust = ITrustService(_trust);
        registry = IRegistryService(_registry);
        compliance = IComplianceService(_compliance);
    }

    /**
     * @dev 토큰 소수점 자릿수 반환
     */
    function decimals() public view override returns (uint8) {
        return _decimals;
    }

    /**
     * @dev 발행사/마스터 권한 확인
     */
    modifier onlyIssuerOrMaster() {
        uint256 role = trust.roleOf(msg.sender);
        require(role == Constants.ROLE_ISSUER || role == Constants.ROLE_MASTER, "Not issuer/master");
        _;
    }

    /**
     * @dev 전송 가능 여부 확인 (정지/락 체크)
     */
    modifier canTransfer(address from, address to, uint256 amount) {
        require(!paused(), "Transfers paused");

        // 락킹 확인
        LockInfo memory lock = _locks[from];
        if (lock.amountLocked > 0 && block.timestamp < lock.releaseTime) {
            uint256 spendable = balanceOf(from) - lock.amountLocked;
            require(amount <= spendable, "Locked balance");
        }

        // 컴플라이언스 검증
        (bool ok, string memory reason) = compliance.validateTransfer(from, to, amount);
        require(ok, reason);

        _;
    }

    /**
     * @dev 토큰 발행
     */
    function issueTokens(address to, uint256 value) external onlyIssuerOrMaster nonReentrant returns (bool) {
        // 사전 검증
        (bool preOk, ) = compliance.preIssuanceCheck(to, value);
        require(preOk, "Pre-issuance blocked");

        // 본 검증
        (bool ok, string memory reason) = compliance.validateIssuance(to, value);
        require(ok, reason);

        _mint(to, value);
        totalIssued += value;

        emit Issued(to, value);
        return true;
    }

    /**
     * @dev 락과 함께 토큰 발행
     */
    function issueTokenWithLocking(
        address to,
        uint256 value,
        uint256 valueLocked,
        string calldata reason,
        uint64 releaseTime
    ) external onlyIssuerOrMaster nonReentrant returns (bool) {
        // 기본 발행 검증
        (bool preOk, ) = compliance.preIssuanceCheck(to, value);
        require(preOk, "Pre-issuance blocked");

        (bool ok, string memory reasonStr) = compliance.validateIssuance(to, value);
        require(ok, reasonStr);

        _mint(to, value);
        totalIssued += value;

        // 락 설정
        if (valueLocked > 0) {
            require(valueLocked <= balanceOf(to), "Lock > balance");
            require(releaseTime > block.timestamp, "Invalid release time");

            _locks[to] = LockInfo({
                amountLocked: valueLocked,
                releaseTime: releaseTime,
                reason: reason
            });

            emit LockSet(to, valueLocked, releaseTime, reason);
        }

        emit IssuedWithLock(to, value, valueLocked, releaseTime, reason);
        return true;
    }

    /**
     * @dev 토큰 소각
     */
    function burn(address who, uint256 value, string calldata reason) external onlyIssuerOrMaster nonReentrant returns (bool) {
        (bool ok, string memory reasonStr) = compliance.validateBurn(who, value);
        require(ok, reasonStr);

        require(balanceOf(who) >= value, "Insufficient balance");

        _burn(who, value);
        emit Burned(who, value, reason);
        return true;
    }

    /**
     * @dev 강제 회수
     */
    function seize(address from, address to, uint256 value, string calldata reason) external onlyIssuerOrMaster nonReentrant returns (bool) {
        (bool ok, string memory reasonStr) = compliance.validateSeize(from, to, value);
        require(ok, reasonStr);

        require(to != address(0), "Zero to");
        require(balanceOf(from) >= value, "Insufficient balance");

        // 락 고려
        LockInfo memory lock = _locks[from];
        if (lock.amountLocked > 0 && block.timestamp < lock.releaseTime) {
            uint256 spendable = balanceOf(from) - lock.amountLocked;
            require(value <= spendable, "Locked balance");
        }

        _transfer(from, to, value);
        emit Seized(from, to, value, reason);
        return true;
    }

    /**
     * @dev 일시정지
     */
    function pause() external onlyIssuerOrMaster {
        _pause();
    }

    /**
     * @dev 재개
     */
    function unpause() external onlyIssuerOrMaster {
        _unpause();
    }

    /**
     * @dev 락 설정
     */
    function setLock(address to, uint256 amount, uint64 releaseTime, string calldata reason) external onlyIssuerOrMaster {
        require(to != address(0), "Zero address");
        require(amount <= balanceOf(to), "Lock > balance");
        require(releaseTime > block.timestamp, "Invalid release time");

        _locks[to] = LockInfo({
            amountLocked: amount,
            releaseTime: releaseTime,
            reason: reason
        });

        emit LockSet(to, amount, releaseTime, reason);
    }

    /**
     * @dev 락 해제
     */
    function releaseLock(address to) external onlyIssuerOrMaster {
        LockInfo storage lock = _locks[to];
        require(lock.amountLocked > 0, "No lock");
        require(block.timestamp >= lock.releaseTime, "Lock not expired");

        uint256 amount = lock.amountLocked;
        lock.amountLocked = 0;
        lock.releaseTime = 0;
        lock.reason = "";

        emit LockReleased(to, amount);
    }

    /**
     * @dev 전송 (컴플라이언스 검증 포함)
     */
    function transfer(address to, uint256 amount) public override canTransfer(msg.sender, to, amount) returns (bool) {
        return super.transfer(to, amount);
    }

    /**
     * @dev 승인 전송 (컴플라이언스 검증 포함)
     */
    function transferFrom(address from, address to, uint256 amount) public override canTransfer(from, to, amount) returns (bool) {
        return super.transferFrom(from, to, amount);
    }

    /**
     * @dev 보유자 수 조회
     */
    function walletCount() external view returns (uint256) {
        return _holders.length;
    }

    /**
     * @dev 보유자 주소 조회
     */
    function getWalletAt(uint256 index) external view returns (address) {
        require(index < _holders.length, "Index out of bounds");
        return _holders[index];
    }

    /**
     * @dev 투자자 중심 잔액 조회
     */
    function balanceOfInvestor(string calldata investorId) external view returns (uint256 total) {
        address[] memory wallets = registry.walletsOf(investorId);
        for (uint256 i = 0; i < wallets.length; i++) {
            total += balanceOf(wallets[i]);
        }
    }

    /**
     * @dev 사전 전송 체크
     */
    function preTransferCheck(address from, address to, uint256 value) external view returns (bool ok, string memory reason) {
        if (paused()) return (false, "Paused");

        LockInfo memory lock = _locks[from];
        if (lock.amountLocked > 0 && block.timestamp < lock.releaseTime) {
            uint256 spendable = balanceOf(from) - lock.amountLocked;
            if (value > spendable) return (false, "Locked balance");
        }

        return compliance.preTransferCheck(from, to, value);
    }

    /**
     * @dev 락 정보 조회
     */
    function getLockInfo(address addr) external view returns (uint256 amountLocked, uint64 releaseTime, string memory reason) {
        LockInfo memory lock = _locks[addr];
        return (lock.amountLocked, lock.releaseTime, lock.reason);
    }

    /**
     * @dev 사용 가능한 잔액 조회 (락 고려)
     */
    function spendableBalance(address addr) external view returns (uint256) {
        uint256 balance = balanceOf(addr);
        LockInfo memory lock = _locks[addr];

        if (lock.amountLocked > 0 && block.timestamp < lock.releaseTime) {
            return balance - lock.amountLocked;
        }

        return balance;
    }

    /**
     * @dev 외부 서비스 업데이트
     */
    function updateServices(address _trust, address _registry, address _compliance) external onlyIssuerOrMaster {
        trust = ITrustService(_trust);
        registry = IRegistryService(_registry);
        compliance = IComplianceService(_compliance);
    }

    /**
     * @dev 내부 전송 로직 (보유자 목록 관리 포함)
     */
    function _update(address from, address to, uint256 value) internal override {
        super._update(from, to, value);

        // 보유자 목록 동기화
        _syncHoldersOnDecrease(from);
        _syncHoldersOnIncrease(to);
    }

    /**
     * @dev 보유자 목록에서 제거
     */
    function _syncHoldersOnDecrease(address addr) internal {
        if (_isHolder[addr] && balanceOf(addr) == 0) {
            _isHolder[addr] = false;

            // 배열에서 제거
            uint256 len = _holders.length;
            for (uint256 i = 0; i < len; i++) {
                if (_holders[i] == addr) {
                    if (i != len - 1) {
                        _holders[i] = _holders[len - 1];
                    }
                    _holders.pop();
                    break;
                }
            }
        }
    }

    /**
     * @dev 보유자 목록에 추가
     */
    function _syncHoldersOnIncrease(address addr) internal {
        if (!_isHolder[addr] && balanceOf(addr) > 0) {
            _isHolder[addr] = true;
            _holders.push(addr);
        }
    }
}

